import React, { useState } from 'react';

function BoxGrid() {
    const [selectedBox, setSelectedBox] = useState(null);
    const [buttonColor, setButtonColor] = useState('gray');
    const [progressWidth, setProgressWidth] = useState('0%');
    const [progressColor, setProgressColor] = useState('gray');

    const handleBoxSelect = (boxIndex) => {
        setSelectedBox(boxIndex);
        setButtonColor('black');
        setProgressWidth('80%');
        setProgressColor('green');
    };

    return (
        <section>
            <div className="py-16">
                <div className="mx-auto px-6 max-w-6xl">
                    <h1 className="text-3xl text-center text-gray-900 dark:text-white font-semibold">Main Heading</h1>
                    <p className="text-center mt-2 text-gray-600 dark:text-gray-300">Subheading</p>
                    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6 mt-6">
                        {[1, 2, 3, 4].map((index) => (
                            <div
                                key={index}
                                className={`bg-white border border-gray-200 dark:border-gray-800 dark:bg-gray-900 rounded-xl overflow-hidden ${
                                    selectedBox === index ? '' : ''
                                }`}
                                onClick={() => handleBoxSelect(index)}
                            >
                                <img src={`image${index}.jpg`} alt={index} className="w-full h-40 object-cover" />
                                <div className="p-6">
                                    <h2 className="text-xl text-gray-900 dark:text-white font-semibold">Heading {index}</h2>
                                    <p className="mt-2 text-gray-600 dark:text-gray-300">Subheading {index}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="flex justify-center mt-8">
                        <button
                            className={`${
                                buttonColor === 'gray' ? 'bg-gray-500 hover:bg-gray-700' : 'bg-black hover:bg-gray-700'
                            } text-white font-bold py-2 px-4 rounded`}
                        >
                            Continue
                        </button>
                    </div>
                </div>
            </div>
            {selectedBox && (
                <div className="fixed top-0 left-0 w-full h-1 bg-blue-500" style={{ zIndex: '9999' }}>
                    <div style={{ width: progressWidth, backgroundColor: progressColor, height: '4px', marginTop: '50px' }}></div>
                </div>
            )}
        </section>
    );
}

export default BoxGrid;
